import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "./utils/global.context";

const Card = ({ name, username, id }) => {
  const { theme, handleChangeFavs } = useContext(ContextGlobal);
  const addFav = (e, name, username, id) => {
    e.stopPropagation();
    e.preventDefault();
    const currentDentist = { name: name, username: username, id: id };
    handleChangeFavs(currentDentist);
  };

  return (
    <Link
      to={`/detail/${id}`}
      className="card"
      style={{ backgroundColor: theme.cardBackground, color: theme.font }}
    >
      <img src="/images/doctor.jpg" alt="doc-img" />
      <p>
        <b>Name:</b> {name}
      </p>
      <p>
        <b>Username: </b>
        {username}
      </p>
      <button
        onClick={(e) => addFav(e, name, username, id)}
        className="favButton"
      >
        Add fav
      </button>
    </Link>
  );
};

export default Card;
