import {
  createContext,
  useCallback,
  useEffect,
  useMemo,
  useReducer,
  useState,
} from "react";

export const initialState = {
  themes: {
    light: {
      font: "black",
      backgroundColor: "#EEF1FF",
      navBackground: "#D2DAFF",
      cardBackground: "#c2d4ff",
    },
    dark: {
      font: "white",
      backgroundColor: "#352F44",
      navBackground: "#5C5470",
      cardBackground: "#5e5a6e",
    },
  },
  data: [],
};

export const ContextGlobal = createContext(initialState);

export const themeReducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_THEME":
      if (action.payload === initialState.themes.dark)
        return initialState.themes.light;
      if (action.payload === initialState.themes.light)
        return initialState.themes.dark;
      return state;
    default:
      return state;
  }
};

export const ContextProvider = ({ children }) => {
  const { themes, data } = initialState;
  //Aqui deberan implementar la logica propia del Context, utilizando el hook useMemo
  // const [theme, setTheme] = useState(themes.light);
  const [apiData, setApiData] = useState(data);

  const [theme, dispatch] = useReducer(themeReducer, themes.light);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(
        "https://jsonplaceholder.typicode.com/users"
      ).then((data) => data.json());
      setApiData(response);
    };
    fetchData();
  }, []);

  // const handleChangeTheme = useCallback(() => {
  //   if (theme === themes.dark) setTheme(themes.light);
  //   if (theme === themes.light) setTheme(themes.dark);
  // }, [theme, themes.dark, themes.light]);

  const handleChangeTheme = (theme) => {
    dispatch({ type: "CHANGE_THEME", payload: theme });
  };

  // const themeProviderValue = useMemo(
  //   () => ({ theme, handleChangeTheme }),
  //   [theme, handleChangeTheme]
  // );

  return (
    <ContextGlobal.Provider value={{ theme, handleChangeTheme, apiData }}>
      {children}
    </ContextGlobal.Provider>
  );
};
