import { createContext, useEffect, useMemo, useReducer, useState } from "react";

export const initialState = {
  themes: {
    light: {
      font: "black",
      backgroundColor: "#EEF1FF",
      navBackground: "#D2DAFF",
      cardBackground: "#c2d4ff",
    },
    dark: {
      font: "white",
      backgroundColor: "#352F44",
      navBackground: "#5C5470",
      cardBackground: "#5e5a6e",
    },
  },
  data: [],
};

export const ContextGlobal = createContext(initialState);

export const themeReducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_THEME":
      if (action.payload === initialState.themes.dark)
        return initialState.themes.light;
      if (action.payload === initialState.themes.light)
        return initialState.themes.dark;
      break;
    default:
      return state;
  }
};

export const favsReducer = (state, action) => {
  switch (action.type) {
    case "ADD_FAV":
      let currentDentist = action.payload;
      let dentistArray = currentDentist.filter((item) => item.id === state.id);
      if (dentistArray.length !== 0) {
        alert("Este dentista ya se encuentra en Favs.");
        return;
      } else {
        let newFavs = [...state, action.payload];
        localStorage.setItem("favs", JSON.stringify(newFavs));
        alert("¡Este dentista se agregó a Favs con éxito!");
        return;
      }
    default:
      return state;
  }
};

export const ContextProvider = ({ children }) => {
  const { themes, data } = initialState;
  const [apiData, setApiData] = useState(data);
  const [theme, dispatch] = useReducer(themeReducer, themes.light);
  const [favs, dispatchFavs] = useReducer(favsReducer, {
    favs: JSON.parse(localStorage.getItem("favs")) || [],
  });

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(
        "https://jsonplaceholder.typicode.com/users"
      ).then((data) => data.json());
      setApiData(response);
    };
    fetchData();
  }, []);

  const themeProviderValue = useMemo(
    () => ({
      theme,
      handleChangeTheme: (theme) =>
        dispatch({ type: "CHANGE_THEME", payload: theme }),
    }),
    [theme, dispatch]
  );

  const favsProviderValue = useMemo(
    () => ({
      favs,
      handleChangeFavs: (fav) =>
        dispatchFavs({ type: "ADD_FAV", payload: fav }),
    }),
    [favs, dispatchFavs]
  );

  return (
    <ContextGlobal.Provider
      value={{ ...themeProviderValue, ...favsProviderValue, apiData }}
    >
      {children}
    </ContextGlobal.Provider>
  );
};
