  };

  return (
    <Link to={`/detail/${id}`}>
      <div className="card">
        <img src="/images/doctor.jpg" alt="doc-img" />

        <p>Name: {name}</p>
        <p>Username: {username}</p>
        <p>Id: {id}</p>

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
