import React, { useContext } from "react";
import { Link } from "react-router-dom";
import "./styles.css";
import { ContextGlobal } from "../utils/global.context";

const Card = ({ name, username, id }) => {
  const { theme } = useContext(ContextGlobal);
  const addFav = (e) => {
    e.stopPropagation();
    e.preventDefault();
    let currentFavs = localStorage.getItem("favs") ?? [];
    currentFavs = [...currentFavs, { name: name, username: username, id: id }];
    localStorage.setItem("favs", JSON.stringify(currentFavs));
  };

  return (
    <div
        className="card"
        style={{ backgroundColor: theme.cardBackground, color: theme.font }}
      >
    <Link to={`/detail/${id}`}>
        <img src="/images/doctor.jpg" alt="doc-img" />
        <p>
          <b>Name:</b> {name}
        </p>
        <p>
          <b>Username: </b>
          {username}
        </p>
        <button onClick={(e) => addFav(e)} className="favButton">
          Add fav
        </button>
    </Link>
      </div>
  );
};

export default Card;
